import de.espirit.firstspirit.agency.StoreAgent;
import de.espirit.firstspirit.agency.StoreElementAgent;

import java.util.*;


// ------------------------------------------------ The normal configuration of the delta generation ---------------------------------------------------
// create new DeltaGeneration
deltaGeneration = DeploymentUtil.createDeltaGeneration(context);

deltaGeneration.dependencyRules(EnumSet.of(DependencyRule.PROPAGATE_PAGE_CHANGES, 
			     DependencyRule.PROPAGATE_SECTION_CHANGES,
			     DependencyRule.PROPAGATE_MEDIA_CHANGES_TO_PAGESTORE,
			     DependencyRule.PROPAGATE_MEDIA_CHANGES_TO_SITESTORE));


// search for all changed elements inside the project
DeltaGeneration.ChangeSet changeSet = deltaGeneration.levelRule(0,2).calculateChangeSet();

// Configures the first generation task (after the current script task) to generate the found changed elements.
changeSet.configureGenerateTask();

// Only a log information indicating whether it is a full generation.
context.logInfo("***** isFullGenerate: " + changeSet.isFullGenerate());

// /----------------------------------------------- The normal configuration of the delta generation ---------------------------------------------------

// ------------------------------------------------ For the generation of ContentSelect pages. ---------------------------------------------------

/**
* Here you can configure which templates should be taken into account when creating records.
* Key: Entity type name
* Value: List of uids
*/
Map entityContentSelects = new HashMap();
entityContentSelects.put("news", Arrays.asList("latest_news", "news_json"));


/**
* Search for the next generation task
*
* @return the task of the next generation or null if none was found
*/
GenerateTask getFirstGenerationTask() {
    final List tasks = context.getTasks();
    for (int i = context.getTaskIndex() + 1; i < tasks.size(); i++) {
        final ScheduleTask task = tasks.get(i);
        if (task instanceof GenerateTask) {
            return ((GenerateTask) task);
        }
    }
    return null;
}

/**
* Attempts to determine which of the configured ContentSelect templates might provide a
* different result based on the datasets generated.
*
* @param generateTask
* @return
*/
List getContentSelectTemplates(GenerateTask generateTask) {
    Collection entityStartNodes = generateTask.getEntityStartNodes();
    List contentSelectTemplates = new ArrayList();
    // Are there any changed datasets
    if (!entityStartNodes.isEmpty() && !entityContentSelects.isEmpty()) {
        StoreAgent storeAgent = context.requireSpecialist(StoreAgent.TYPE);
        SiteStoreRoot siteStoreRoot = (SiteStoreRoot) storeAgent.getStore(Store.Type.SITESTORE);
        Iterator entityEntryIterator = entityStartNodes.iterator();
        while (entityEntryIterator.hasNext() && !entityContentSelects.isEmpty()) {            
            GenerateTask.EntityEntry entityStartNode = entityEntryIterator.next();
            IDProvider storeElement = siteStoreRoot.getStoreElement(entityStartNode.getPageRefId());
            if (storeElement instanceof PageRef) {
                PageRef pageRef = (PageRef) storeElement;
                Content2Params content2Params = pageRef.getContent2Params();
                String entityTypeName = content2Params.getTableTemplate().getEntityType().getName();
                if (entityContentSelects.containsKey(entityTypeName)) {
                    contentSelectTemplates.addAll(entityContentSelects.get(entityTypeName));
                    contentSelectTemplates.remove(entityTypeName);
                }
            }
        }
    }
    return contentSelectTemplates;
}

/**
* Depending on the datasets to be generated and the given ContentSelect templates, an attempt is made to determine which page references need to be generated.
*
* @param contentSelectTemplates list of uids of templates
* @return a list of PageRefs
*/
List getContentSelectPageRefs(List contentSelectTemplates) {
    StoreElementAgent storeElementAgent = context.requireSpecialist(StoreElementAgent.TYPE);
    List pageRefsToAdd = new ArrayList();
    for (String contentSelectTemplate : contentSelectTemplates) {
        Template template = (Template) storeElementAgent.loadStoreElement(contentSelectTemplate, IDProvider.UidType.TEMPLATESTORE, false);
        // Only works for section and page templates
        if (template.getType() == Template.PAGE_TEMPLATE
                || template.getType() == Template.SECTION_TEMPLATE) {
            ReferenceEntry[] incomingReferences = template.getIncomingReferences();
            // Only pages and the pages to sections (ignore gcas, datasets...)
            for (ReferenceEntry incomingReference : incomingReferences) {
                if (!incomingReference.isBroken() && incomingReference.isType(ReferenceEntry.PAGE_STORE_REFERENCE)) {
                    pageRefsToAdd.addAll(getPageRefs(incomingReference.getReferencedElement()));
                }
            }
        }
    }
    return pageRefsToAdd;
}

/**
* Tries to determine all associated pagerefs.
*
* @param referencedElement
* @return a collection of PageRefs
*/
Collection getPageRefs(IDProvider referencedElement) {
    Page page = getPage(referencedElement);
    if (page != null) {
        return page.getPageRefs();
    }
    return Collections.emptyList();
}

/**
* Tries to determine the associated page.
*
* @param referencedElement
* @return a page or null
*/
Page getPage(IDProvider referencedElement) {
    if (referencedElement instanceof Page) {
        return ((Page) referencedElement);
    } else if (referencedElement.getParent() != null) {
        return getPage(referencedElement.getParent());
    } else {
        return null;
    }
}

// If it is a full generation, we do not need to add additional pages with a contentSelect.
if (!changeSet.isFullGenerate()) {
    GenerateTask generateTask = getFirstGenerationTask();
    if (generateTask != null) {
        List<String> contentSelectTemplates = getContentSelectTemplates(generateTask);
        if (!contentSelectTemplates.isEmpty()) {
            List<PageRef> pageRefsToAdd = getContentSelectPageRefs(contentSelectTemplates);
            generateTask.getStartNodes().addAll(pageRefsToAdd);
        }
    }
}
// /------------------------------------------------ For the generation of ContentSelect pages. ---------------------------------------------------

