import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.zip.ZipEntry;
import java.util.zip.ZipOutputStream;


	 zipFile(File fileToZip, String fileName, ZipOutputStream zipOut) {
		context.logInfo("Zip file '" + fileToZip.getPath() + "' successfully created");
		if (fileToZip.isHidden()) {
			return;
		}
		if (fileToZip.isDirectory()) {
			if (fileName.endsWith("/")) {
				zipOut.putNextEntry(new ZipEntry(fileName));
				zipOut.closeEntry();
			} else {
				zipOut.putNextEntry(new ZipEntry(fileName + "/"));
				zipOut.closeEntry();
			}
			File[] children = fileToZip.listFiles();
			for (File childFile : children) {
				zipFile(childFile, fileName + "/" + childFile.getName(), zipOut);
			}
			return;
		}
		FileInputStream fis = new FileInputStream(fileToZip);
		ZipEntry zipEntry = new ZipEntry(fileName);
		zipOut.putNextEntry(zipEntry);
		byte[] bytes = new byte[1024];
		int length;
		while ((length = fis.read(bytes)) >= 0) {
			zipOut.write(bytes, 0, length);
		}
		fis.close();
	}

	zipAll() {
		String sourceFile = context.getPath();
		Path sourceFolderPath = Paths.get(sourceFile);
		File sourceFolderFile = sourceFolderPath.toFile();
		if (sourceFolderFile.exists() && sourceFolderFile.isDirectory()) {
			File[] files = sourceFolderFile.listFiles();
			Path zip = Files.createFile(Paths.get(context.getPath(), "website.zip"));
			ZipOutputStream zipOut = new ZipOutputStream(Files.newOutputStream(zip));
			try {
				for (File file : files) {
					context.logInfo("Zip file '" + file.getPath() + "' found");
					 if (!file.getPath().endsWith("website.zip")) {
						context.logInfo("Zip file '" + file.getPath() + "' successfully created");
						zipFile(file, file.getName(), zipOut);	
					}	   
				}
			} finally {
				zipOut.close();
			}
		}
	}
context.logInfo("Start");
zipAll();
context.logInfo("End");

